      document.getElementsByClassName('pointerCircle')[0].style.left = (event.offsetX - 18) + 'px'
      document.getElementsByClassName('pointerCircle')[0].style.top = (event.offsetY - 18) + 'px'
      document.getElementsByClassName('pointerCircle')[0].classList.add('fadeIn')
      document.getElementsByClassName('detailLine')[0].style.left = (932 - 447 + event.offsetX + 10) + 'px'
      document.getElementsByClassName('detailLine')[0].style.top = (152 + event.offsetY) + 'px'
      document.getElementsByClassName('detailLine')[0].classList.add('fadeIn')
      document.getElementsByClassName('detailCircle')[0].style.left = (932 - 447 + event.offsetX + 18 + 50) + 'px'
      document.getElementsByClassName('detailCircle')[0].style.top = (134 + event.offsetY) + 'px'
      document.getElementsByClassName('detailCircle')[0].classList.add('fadeHeight')
      this.thisCity = name + '市'
      this.thisPopulation = parseInt(1000000 * Math.random())
      this.thisHouse = parseInt(100000 * Math.random())
      this.pointerCircle = true
      return false
    },
    removeAll () {
      this.pointerCircle = false
      document.getElementsByClassName('pointerCircle')[0].classList.remove('fadeIn')
      document.getElementsByClassName('detailLine')[0].classList.remove('fadeIn')
      document.getElementsByClassName('detailCircle')[0].classList.remove('fadeHeight')
      document.getElementsByClassName('detailCircle')[0].classList.add('fadeHeightZero')
      this.hideTimeout = setTimeout(function () {
        document.getElementsByClassName('detailCircle')[0].style.display = 'none'
        document.getElementsByClassName('detailCircle')[0].classList.remove('fadeHeightZero')
      }, 1000)
    },
    housePillar () {
      var self = this
      this.pillarHouse.times = ['第41周', '第42周', '第43周', '第44周', '第45周', '第46周', '第47周']
      this.pillarHouse.dates = [100, 300, 200, 90, 125, 254, 168]
      window.setInterval(function () {
        self.pillarHouse.dates = [200, 300, 100, 200, 452, 105, 253]
      }, this.timeInterval)
    },
    peoplePillar () {
      var self = this
      this.pillarPeople.times = ['第41周', '第42周', '第43周', '第44周', '第45周', '第46周', '第47周']
      this.pillarPeople.dates = [125, 150, 135, 134, 95, 105, 135]
      window.setInterval(function () {
        self.pillarPeople.dates = [200, 300, 100, 200, 452, 105, 253]
      }, this.timeInterval)
    }
  },
  created () {
    this.provinceDetail = {
      'area': 101258,
      'people': 8836.26,
      'money': 68473.46,
      'district': 17
    }
  },
  mounted () {
    this.housePillar()
    this.peoplePillar()
  }
}

